import React from "react";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { Link } from "react-router-dom";



export default class Detail extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      post: {},
    };
  }

  componentDidMount() {
    const id = this.props.match.params.postId;
    fetch(`https://itp-final.herokuapp.com/api/posts/${id}`)
      .then((response) => {
        return response.json();
      })
      .then((json) => {
        this.setState({ post: json });
      });
  }

  deletePost() {
    const isDeleteConfirmed = window.confirm(
      "Are you sure you want to delete this post?"
    );

    if (!isDeleteConfirmed) {
      return;
    }

    fetch(`https://itp-final.herokuapp.com/api/posts/${this.state.post.id}`, {
      method: "DELETE",
    }).then((json) => {
      toast.success(`${this.state.post.title} was deleted`);
      this.props.history.push("/"); 
    });
  }

  render() {
    return (
      <>
        <h1>{this.state.post.title}</h1>
        <p>{this.state.post.body}</p>
        <Link
            className="btn btn-primary"
            to={`/board/${this.props.match.params.postId}/edit`}
          >
            Edit
          </Link>
        <button
          type="button"
          className="btn btn-danger"
          onClick={() => {
            this.deletePost();
          }}
        >
          Delete
        </button>

        {this.state.isModalOpen && (
          <Modal
            title="My modal title"
            body={() => {
              return <p>Are you sure you want to delete</p>;
            }}
            onClose={() => {
              this.setState({ isModalOpen: false });
            }}
          />
        )}
      </>
    );
  }
}
