  //       body: this.state.post.body,
  //       categoryId: this.state.post.categoryId,
  //       url: this.state.post.url,
  //       isFavorite: this.state.isFavorite
  //     }),
  //     headers: {
  //       "Content-type": "application/json",
  //     },
  //   })
  //     .then((response) => {
  //       return response.json();
  //     })
  //     .then((json) => {
  //       toast.success(`${json.title} was successfully updated`);
  //       // this.props.history.push("/");
  //     });
  // }

  render() {
    return (
      <>
        <h1>{this.state.title}</h1>
        <p>{this.state.body}</p>
        <Link
            className="btn btn-primary"
            to={`/board/${this.props.match.params.postId}/edit`}
          >
            Edit
          </Link>
        <button
          type="button"
          className="btn btn-danger"
          onClick={() => {
            this.deletePost();
          }}
        >
          Delete
        </button>
        <button
          type="button"
          className="btn btn-danger"
          onClick={() => {
            if(this.state.post.isFavorite === false){
              this.setState({ isFavorite: true})
            }
          }}
        >
          Favorite
        </button>
      </>
    );
  }
}
