import React from "react";
import "./Upload.css";


export default class App extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            title: "",
            body: "",
            categoryId: "", 
            url: ""
        };
    }

    handleTitleChange(event) {
        this.setState({ title: event.target.value });
    }


    render() {
        return (
            <div className="container Upload">
                <form >

                    <div className="my-3">
                        <label htmlFor="title" className="form-label"> Image Title
                        </label>

                        <input type="name" className="form-control" id="title" value={this.state.title}
                        onChange={this.handleTitleChange}/>
                    </div>

                    <div className="mb-3">
                        <label htmlFor="description" className="form-label">Description
                        </label>

                        <textarea type="description" className="form-control" id="description" value={this.state.body}/>
                    </div>

                    <div className="mb-3">
                        <label className="form-label">Category</label>
                        <select className="form-select form-select-lg">
                            <option>--Select a Category--</option>
                            <option value="0">Holiday</option>
                            <option value="1">Flower</option>
                            <option value="2">Nature</option>
                        </select>
                    </div>

                    <div className="my-3">
                        <label htmlFor="link" className="form-label" value={this.state.url}> Image Link
                        </label>

                        <input type="url" className="form-control" id="link" />
                    </div>

                    <buttion type="submit" className="btn btn-primary">Upload</buttion>


                </form>
            </div>
        );
    }
}
