import React from "react";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { Link } from "react-router-dom";
// import Favorite from "./../../components/Favorite";


export default class Detail extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      title: "",
      body: "",
      categoryId: "",
      url: "",
      id: "",
      isFavorite: false
    };
  }

  componentDidMount() {
    const id = this.props.match.params.postId;
    fetch(`https://itp-final.herokuapp.com/api/posts/${id}`)
      .then((response) => {
        return response.json();
      })
      .then((json) => {
        this.setState({ 
          title: json.title,
        body: json.body,
        categoryId: json.categoryId,
        url: json.url,
        id: json.id,
        isFavorite: json.isFavorite });
      });
  }

  deletePost() {
    const isDeleteConfirmed = window.confirm(
      "Are you sure you want to delete this post?"
    );

    if (!isDeleteConfirmed) {
      return;
    }

    fetch(`https://itp-final.herokuapp.com/api/posts/${this.state.post.id}`, {
      method: "DELETE",
    }).then((json) => {
      toast.success(`${this.state.post.title} was deleted`);
      this.props.history.push("/"); 
    });
  }

  addFavorite() {
    fetch(`https://itp-final.herokuapp.com/api/posts/${this.state.id}`, {
      method: "PUT",
      body: JSON.stringify({
        title: this.state.title,
        body: this.state.body,
        categoryId: this.state.categoryId,
        url: this.state.url,
        isFavorite: this.state.isFavorite
      }),
      headers: {
        "Content-type": "application/json",
      },
    })
      .then((response) => {
        return response.json();
      })
      .then((json) => {
        toast.success(`${json.title} was successfully updated`);
        this.props.history.push("/");
      });
  }

  render() {
    return (
      <>
        <h1>{this.state.title}</h1>
        <p>{this.state.body}</p>
        <Link
            className="btn btn-primary"
            to={`/board/${this.props.match.params.postId}/edit`}
          >
            Edit
          </Link>
        <button
          type="button"
          className="btn btn-danger"
          onClick={() => {
            this.deletePost();
          }}
        >
          Delete
        </button>
        <button
          type="button"
          className="btn btn-danger"
          onClick={() => {
              this.addFavorite();
          }}
        >
          Favorite
        </button>
      </>
    );
  }
}
