import "./Home.css";
import React from "react";
// import { Link } from "react-router-dom";
import Modal from "./../../components/Modal";

export default class Home extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      posts: [],
      isModalOpen: false,
    };
  }

  componentDidMount() {
    document.title = "Home";
    fetch("https://itp-final.herokuapp.com/api/posts")
      .then((response) => {
        return response.json();
      })
      .then((json) => {
        this.setState({ posts: json });
      });
  }

  render() {
    return (
      <div className="Home">
        {this.state.posts.map((post) => {
          return (
            <>
              <div key={post.id}>
                <img 
                  onClick={() => {
                    this.setState({ isModalOpen: true });
                  }}
                  className="image"
                  src={post.url}
                  alt={post.title}
                />

              {this.state.isModalOpen && (
                <Modal
                key={post.id}
                  title={post.title}
                  body={() => {
                    return <p>Are you sure you want to delete</p>;
                  }}
                  onClose={() => {
                    this.setState({ isModalOpen: false });
                  }}
                  path={`/board/${post.id}`}
                />
              )}
              </div>
            </>
          );
        })}
      </div>
    );
  }
}
