import React from "react";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { Link } from "react-router-dom";
import Favorite from "./../../components/Favorite";
import "./Detail.css";

export default class Detail extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      title: "",
      body: "",
      categoryId: "",
      url: "",
      id: "",
      isFavorite: null,
      comments: [],
    };
  }

  componentDidMount() {
    const id = this.props.match.params.postId;
    fetch(`https://itp-final.herokuapp.com/api/posts/${id}`)
      .then((response) => {
        return response.json();
      })
      .then((json) => {
        this.setState({
          title: json.title,
          body: json.body,
          categoryId: json.categoryId,
          url: json.url,
          id: json.id,
          isFavorite: json.isFavorite,
        });
        console.log(json);
      });
  }

  fetchComments() {
    fetch(`https://itp-final.herokuapp.com/api/comments`)
      .then((response) => {
        return response.json();
      })
      .then((comments) => {
        this.setState({ comments });
      });
  }

  deletePost() {
    const isDeleteConfirmed = window.confirm(
      "Are you sure you want to delete this post?"
    );

    if (!isDeleteConfirmed) {
      return;
    }

    fetch(`https://itp-final.herokuapp.com/api/posts/${this.state.id}`, {
      method: "DELETE",
    }).then((json) => {
      toast.success(`${this.state.title} was deleted`);
      this.props.history.push("/");
    });
  }

  addFavorite() {
    fetch(`https://itp-final.herokuapp.com/api/posts/${this.state.id}`, {
      method: "PUT",
      body: JSON.stringify({
        title: this.state.title,
        body: this.state.body,
        categoryId: this.state.categoryId,
        url: this.state.url,
        isFavorite: true,
      }),
      headers: {
        "Content-type": "application/json",
      },
    })
      .then((response) => {
        return response.json();
      })
      .then((json) => {
        console.log(json);
        toast.success(
          `${this.state.title} was successfully added to Favorites`,
          { theme: "colored" }
        );
        this.props.history.push("/about");
      });
  }

  removeFavorite() {
    fetch(`https://itp-final.herokuapp.com/api/posts/${this.state.id}`, {
      method: "PUT",
      body: JSON.stringify({
        title: this.state.title,
        body: this.state.body,
        categoryId: this.state.categoryId,
        url: this.state.url,
        isFavorite: false,
      }),
      headers: {
        "Content-type": "application/json",
      },
    })
      .then((response) => {
        return response.json();
      })
      .then((json) => {
        console.log(json);
        toast.success(
          `${this.state.title} was successfully removed from Favorites`,
          { theme: "colored" }
        );
        this.props.history.push("/");
      });
  }

  render() {
    return (
      <div className="Detail">
        <h1 className="title">{this.state.title}</h1>
        <button
          // type="submit"
          className="btn btn-outline-dark btn-lg like"
          onClick={() => {
            !this.state.isFavorite ? this.addFavorite() : this.removeFavorite();
          }}
        >
          <Favorite likeValue={this.state.isFavorite} />
        </button>
        <p>{this.state.body}</p>
        <div className="row">
          <div className="col-6">
            <img className="img" src={this.state.url} alt="" />
            <div className="group">
              <button
                type="button"
                className="btn btn-danger btn-lg bttn"
                onClick={() => {
                  this.deletePost();
                }}
              >
                Delete
              </button>
              <Link
                type="button"
                className="btn btn-success btn-lg bttn2"
                to={`/board/${this.props.match.params.postId}/edit`}
              >
                Edit
              </Link>
            </div>
          </div>
          <div className="comments col-6">
            <div className="list-group">
              {this.fetchComments()}
              {this.state.comments &&
                this.state.comments.map((comment) => {
                  if (comment.postId === this.state.id) {
                    return (
                      <div className="list-group-item" key={comment.id}>
                        <div className="d-flex justify-content-between">
                          <h4>{comment.author}</h4>
                          <small>3 days ago</small>
                          <p>{comment.body}</p>
                        </div>
                      </div>
                    );
                  }
                  return <></>;
                })}
            </div>
          </div>
        </div>
      </div>
    );
  }
}
