import React from "react";
import "./Upload.css";

export default class App extends React.Component {
  constructor(props) {
    super(props);

    // The default value of a form control can't be null or undefined.
    // That will result in it being an Uncontrolled Component, which
    // we aren't covering in this class. Uncontrolled Components
    // generally aren't recommended. You can read more about them here:
    // https://reactjs.org/docs/uncontrolled-components.html.
    this.state = {
      name: "",
      email: "",
      country: "", // change to "usa" for default selection
      message: "",
      optInToNewsletter: false // change to true for default check
    };

    // these functions are only created when the component is
    // instantiated for the first time
    this.handleEmailChange = this.handleEmailChange.bind(this);
    this.handleMessageChange = this.handleMessageChange.bind(this);
    this.handleCountryChange = this.handleCountryChange.bind(this);
    this.handleNewsletterChange = this.handleNewsletterChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleEmailChange(event) {
    // this.setState({ email: event.target.value.toUpperCase() });
    this.setState({ email: event.target.value });
  }

  handleCountryChange(event) {
    this.setState({ country: event.target.value });
  }

  handleMessageChange(event) {
    this.setState({ message: event.target.value });
  }

  handleNewsletterChange(event) {
    this.setState({ optInToNewsletter: event.target.checked });
  }

  handleSubmit(event) {
    event.preventDefault();
    console.log(this.state);
  }

  render() {
    return (
      <div className="Upload">
        <form onSubmit={this.handleSubmit}>
          <div className="my-3">
            <label htmlFor="name" className="form-label">
              Name
            </label>

            <input
              type="name"
              className="form-control"
              id="name"
              value={this.state.name}
              // Whenever this component is rendered, a new function is created.
              // This could have performance implications.
              onChange={(event) => {
                this.setState({ name: event.target.value });
              }}
            />
          </div>
          <div className="mb-3">
            <label htmlFor="email" className="form-label">
              Email
            </label>
            <input
              type="email"
              className="form-control"
              id="email"
              placeholder="name@example.com"
              value={this.state.email}
              onChange={this.handleEmailChange}
            />
          </div>
          <div className="mb-3">
            <label className="form-label">Country</label>
            <select
              value={this.state.country}
              onChange={this.handleCountryChange}
              className="form-select form-select-lg"
            >
              <option>--Select a country--</option>
              <option value="usa">United States</option>
              <option value="canada">Canada</option>
              <option value="mexico">Mexico</option>
            </select>
          </div>
          <div className="mb-3">
            <label htmlFor="message" className="form-label">
              Message
            </label>
            <textarea
              className="form-control"
              id="message"
              rows="3"
              value={this.state.message}
              onChange={this.handleMessageChange}
            />
          </div>
          <div className="form-check mb-3">
            <input
              className="form-check-input"
              type="checkbox"
              checked={this.state.optInToNewsletter}
              onChange={this.handleNewsletterChange}
              id="newsletter"
            />
            <label className="form-check-label" htmlFor="newsletter">
              Check if you'd like to opt-in to our newsletter
            </label>
          </div>
          <button type="submit" className="btn btn-primary">
            Submit
          </button>
        </form>
      </div>
    );
  }
}
