import React from "react";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { Link } from "react-router-dom";
// import Favorite from "./../../components/Favorite";


export default class Detail extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      post: {},
      isFavorite: false
    };
  }

  handleFavoriteChange(event) {
    this.setState({ isFavorite: event.target.value });
  }

  componentDidMount() {
    const id = this.props.match.params.postId;
    fetch(`https://itp-final.herokuapp.com/api/posts/${id}`)
      .then((response) => {
        return response.json();
      })
      .then((json) => {
        this.setState({ post: json });
      });
  }

  deletePost() {
    const isDeleteConfirmed = window.confirm(
      "Are you sure you want to delete this post?"
    );

    if (!isDeleteConfirmed) {
      return;
    }

    fetch(`https://itp-final.herokuapp.com/api/posts/${this.state.post.id}`, {
      method: "DELETE",
    }).then((json) => {
      toast.success(`${this.state.post.title} was deleted`);
      this.props.history.push("/"); 
    });
  }

  addFavorite() {
    this.setState({ isFavorite: true });

    fetch(`https://itp-final.herokuapp.com/api/posts/${this.state.post.id}`, {
      method: "PUT",
      body: JSON.stringify({
        title: this.state.post.title,
        body: this.state.post.body,
        categoryId: this.state.post.categoryId,
        url: this.state.post.url,
        isFavorite: true
      }),
      headers: {
        "Content-type": "application/json",
      },
    })
      .then((response) => {
        return response.json();
      })
      .then((json) => {
        toast.success(`${json.title} was successfully updated`);
        // this.props.history.push("/");
      });
  }

  render() {
    return (
      <>
        <h1>{this.state.post.title}</h1>
        <p>{this.state.post.body}</p>
        <Link
            className="btn btn-primary"
            to={`/board/${this.props.match.params.postId}/edit`}
          >
            Edit
          </Link>
        <button
          type="button"
          className="btn btn-danger"
          onClick={() => {
            this.deletePost();
          }}
        >
          Delete
        </button>
        <button
          type="button"
          className="btn btn-danger"
          onClick={() => {
            this.addFavorite();
          }}
        >
          Favorite
        </button>
      </>
    );
  }
}
