// import "./Favorite.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faHeart } from "@fortawesome/free-solid-svg-icons";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

import React from "react";

export default class Favorite extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      title: "",
      body: "",
      categoryId: "",
      url: "",
      id: "",
      isFavorite: null,
    };
  }

  componentDidMount() {
    const id = this.props.num;
    fetch(`https://itp-final.herokuapp.com/api/posts/${id}`)
      .then((response) => {
        return response.json();
      })
      .then((json) => {
        this.setState({
          title: json.title,
          body: json.body,
          categoryId: json.categoryId,
          url: json.url,
          id: json.id,
          isFavorite: json.isFavorite,
        });
        console.log(json);
      });
  }

  addFavorite(id) {
    fetch(`https://itp-final.herokuapp.com/api/posts/${id}`, {
      method: "PUT",
      body: JSON.stringify({
        title: this.state.title,
        body: this.state.body,
        categoryId: this.state.categoryId,
        url: this.state.url,
        isFavorite: true,
      }),
      headers: {
        "Content-type": "application/json",
      },
    })
      .then((response) => {
        return response.json();
      })
      .then((json) => {
        console.log(json);
        toast.success(
          `${this.state.title} was successfully added to Favorites`
        );
        // this.props.history.push("/about");
      });
  }

  removeFavorite(id) {
    fetch(`https://itp-final.herokuapp.com/api/posts/${id}`, {
      method: "PUT",
      body: JSON.stringify({
        title: this.state.title,
        body: this.state.body,
        categoryId: this.state.categoryId,
        url: this.state.url,
        isFavorite: false,
      }),
      headers: {
        "Content-type": "application/json",
      },
    })
      .then((response) => {
        return response.json();
      })
      .then((json) => {
        console.log(json);
        toast.success(
          `${this.state.title} was successfully removed from Favorites`
        );
        // this.props.history.push("/about");
      });
  }

  render() {
    const add = " Add to Favorites";
    const remove = " Remove";

    return (
      <>
        <button
        className="btn btn-outline-success"
          onClick={() => {
            this.props.likeValue
              ? this.removeFavorite(this.props.num)
              : this.addFavorite(this.props.num)
          }}
        >
          <FontAwesomeIcon
            icon={faHeart}
            color={this.props.likeValue ? "green" : "white"}
            size="sm"
          />
          {this.props.likeValue ? remove : add}
        </button>
      </>
    );
  }
}
